import React, { Component, Fragment } from 'react'
import { DescriptionContain, ImageLogo, FormSearch } from './Style'
import imageTop from '../../img/image-top.svg'

import { graphql } from 'react-apollo'
import gql from 'graphql-tag';


class Description extends Component {
    renderTodoList = function(getList) {
        <ul>
           {getList.map(list, index =>(
               <li>{console.log(list)}</li>
           ))}
        </ul>
    }

    render() {
        const { list } = this.props;
        return(
            <Fragment>
                {list.loading
                ? <p>Teste</p> : this.renderTodoList(list.characters.results) }
            </Fragment>
        )
    }
}


const TodosQuery = gql`
    query {
        characters(page: 2, filter: { name: "rick" }) {
            results {
                name
                image
                species
            }
        }
    }
`

export default graphql(TodosQuery, {
    name: 'list'
})(Description);
