import React, { Component, Fragment } from 'react'
import { DescriptionContain, ImageLogo, FormSearch } from './Style'
import Cards from './../cards/Cards'
import imageTop from '../../img/image-top.svg'

import { graphql } from 'react-apollo'
import gql from 'graphql-tag';


class Description extends Component {
    render() {
        const {list} = this.props;
        return (

            <Fragment>
                <DescriptionContain>
                    <ImageLogo src={imageTop} />
                    <FormSearch>
                        <input placeholder="Search characters" />
                        <button>Search</button>
                    </FormSearch>
                </DescriptionContain>

                <div className="contain">
                 {
                    !list.loading ? 
                    list.characters?.results?.map(function(items) {

                        return <div className="card-items">
                            <img src={items.image}/>

                            <div className="infos-card">
                                <p>{items.name}</p>
                                <span>{items.species}</span>
                            </div>
                            <Cards props={list} />
                        </div>

                    }) : <p>CARREGANDO....</p>
                }
                </div>


            </Fragment>
        )
    }
}


const listQuery = gql`
    query {
        characters(page: 2, filter: { name: "rick" }) {
            results {
                name
                image
                species
            }
        }
    }
`

export default graphql(listQuery, {
    name: 'list'
})(Description);
