import styled from 'styled-components'

export const HeaderContain = styled.header`
    display: flex;
    width: 100%;
    justify-content: center;
    nav {
        width: 100%
    }
`;

export const MenuContain = styled.ul`
    background-color: #9C8CB9;
    width: 100%;

    li {
        text-align: center;
        a {
            color: #fff;
            padding: 0.8rem 0px;
            font-size: 1.25rem;
            display: block;
            border: solid 0.1px #ffffff38;            
        }
    }
`;
