import React, { useState, Fragment } from 'react'
import { DescriptionContain, ImageLogo, FormSearch, ContainerList} from './Style'
import Cards from './../cards/Cards'
import imageTop from '../../img/image-top.svg'
import listQuery from '../../services/query/querys'
import { graphql, Query } from 'react-apollo'
import {useLazyQuery} from '@apollo/react-hooks';

const Description = function (props) {
    const [name, setName] = useState('');
    const { data, error, loading } = useQuery(listQuery, {
        variables: { name }
    });

    let content;
    let sName;

    if (data.user !== undefined && data.user !== null) {
        if (loading) { content = 'Loading User...' }

        if (error) { content = `Error Occur: ${error}` }

        const user = data.user;
        content = `Username: ${ user.name }    ID: ${ user.id }`
    }

    return (
        <Fragment>
        <DescriptionContain>
            <ImageLogo src={imageTop} />
            <FormSearch>
                <input 
                    name="search" 
                    placeholder="Search characters"
                    onChange={(event) => {
                        sName = event.target.value;
                    }}
                />
                <button 
                    type="submit"
                    onClick={(event) => {
                        setName(sName);
                    }} value={name}
                >Search</button>
            </FormSearch>
            <p>{content}</p>
        </DescriptionContain>

        <ContainerList>
           <Cards 
                name='teste'
                image='teste'
                species='teste'
                key='teste'
            />
        </ContainerList>


    </Fragment>
    )
}

export default graphql(listQuery, {
    name: 'list'
})(Description);
