import React, { Component, Fragment } from 'react'
import { DescriptionContain, ImageLogo, FormSearch, ContainerList} from './Style'
import Cards from './../cards/Cards'
import imageTop from '../../img/image-top.svg'

import { graphql } from 'react-apollo'
// import  from 'graphql-tag';
import { gql, useQuery } from "@apollo/client";
class Description extends {Component, useQuery} {
    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
        event.preventDefault();
        const dataInput = new FormData(event.target);
        const stringData = dataInput.get('search');


        const {loading, error, data} = useQuery(listQuery, {
            variables: {stringData}
        })

      }

    render() {
        const {list} = this.props;
        return (

            <Fragment>
                <DescriptionContain>
                    <ImageLogo src={imageTop} />
                    <FormSearch onSubmit={this.handleSubmit}>
                        <input name="search" placeholder="Search characters" />
                        <button type="submit">Search</button>
                    </FormSearch>
                </DescriptionContain>

                <ContainerList>
                    {
                    !list.loading ?  
                    list.characters?.results?.map(function(items) {

                        return <Cards 
                                name={items.name}
                                image={items.image}
                                species={items.species}
                            />

                    }) : <p>CARREGANDO....</p>
                    }
                </ContainerList>


            </Fragment>
        )
    }
}


const listQuery = gql`
    query($name: String!) {
        characters(filter: { name: $name }) {
            results {
                name
                image
                species
            }
        }
    }
`

export default graphql(listQuery, {
    name: 'list'
})(Description);
