import React, { Component, Fragment } from 'react'
import { DescriptionContain, ImageLogo, FormSearch, ContainerList} from './Style'
import Cards from './../cards/Cards'
import imageTop from '../../img/image-top.svg'
import listQuery from '../../services/query/querys'
import { graphql } from 'react-apollo'
import { useLazyQuery } from '@apollo/client';

class Description extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: ''
        }
        const [searchFilter, setSearchFilter] = useState('');
        const [executeSearch, { data }] = useLazyQuery(
            FEED_SEARCH_QUERY
        );
    }

    submitForm(e) {
        e.preventDefault();
        this.props.listQuery({
            variables: {
                name: this.state.name
            }
        })
    }

    render() {
        const {list} = this.props;
        console.log(this.props)
        return (

            <Fragment>
                <DescriptionContain>
                    <ImageLogo src={imageTop} />
                    <FormSearch onSubmit={this.submitForm.bind(this)}>
                        <input 
                            name="search" 
                            placeholder="Search characters"
                            onChange={(e) => setSearchFilter(e.target.value)}
                        />
                        <button type="submit">Search</button>
                    </FormSearch>
                </DescriptionContain>

                <ContainerList>
                    {
                    !list.loading ?  
                    list.characters?.results?.map(function(items, index) {

                        return <Cards 
                                name={items.name}
                                image={items.image}
                                species={items.species}
                                key={index}
                            />

                    }) : <p>CARREGANDO....</p>
                    }
                </ContainerList>


            </Fragment>
        )
    }
}

export default graphql(listQuery, {
    name: 'list'
})(Description);
