import React, { Component, Fragment } from 'react'
import { DescriptionContain, ImageLogo, FormSearch, ContainerList} from './Style'
import Cards from './../cards/Cards'
import imageTop from '../../img/image-top.svg'
import listQuery from '../../services/query/querys'
import { graphql, Query } from 'react-apollo'

class Description extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: ''
        }
    }

    updateState = (e) => {
        this.setState({
            name: e.target.value
        })
    }

    submitForm(e) {
        e.preventDefault();
        console.log(this.state)
    }

    render() {
        const {list} = this.props;
        const {name} = this.state;

        return (

            <Fragment>
                <DescriptionContain>
                    <ImageLogo src={imageTop} />
                    <FormSearch onSubmit={this.submitForm}>
                        <input 
                            name="search" 
                            placeholder="Search characters"
                            onChange={this.updateState}
                            value={name}
                        />
                        <button type="submit">Search</button>
                    </FormSearch>
                </DescriptionContain>

                <ContainerList>
                    {
                    !list.loading ?  
                    list.characters?.results?.map(function(items, index) {

                        return <Cards 
                                name={items.name}
                                image={items.image}
                                species={items.species}
                                key={index}
                            />

                    }) : <p>CARREGANDO....</p>
                    }
                </ContainerList>


            </Fragment>
        )
    }
}

export default graphql(listQuery, {
    name: 'list'
})(Description);
