import React, { Component, Fragment } from 'react'
import { DescriptionContain, ImageLogo, FormSearch, ContainerList} from './Style'
import Cards from './../cards/Cards'
import imageTop from '../../img/image-top.svg'
import listQuery from '../../services/query/querys'
import { graphql, Query } from 'react-apollo'

const Description = function (props) {
    this.state = {
        name: ''
    }
    function updateState(e) {
        this.setState({
            name: e.target.value
        })
    }

    function submitForm(e) {
        e.preventDefault();
        console.log(this.state)
    }
    return (
        <Fragment>
        <DescriptionContain>
            <ImageLogo src={imageTop} />
            <FormSearch onSubmit={submitForm}>
                <input 
                    name="search" 
                    placeholder="Search characters"
                    onChange={updateState}
                />
                <button type="submit">Search</button>
                {/* {console.log(this.state)} */}
            </FormSearch>
        </DescriptionContain>

        <ContainerList>
           <Cards 
                name='teste'
                image='teste'
                species='teste'
                key='teste'
            />
        </ContainerList>


    </Fragment>
    )
}

export default graphql(listQuery, {
    name: 'list'
})(Description);
