import React, { Component, Fragment } from 'react'
import { DescriptionContain, ImageLogo, FormSearch, ContainerList} from './Style'
import Cards from './../cards/Cards'
import imageTop from '../../img/image-top.svg'

import { graphql, useQuery } from 'react-apollo'
import gql from 'graphql-tag';


class Description extends Component {
    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    DogPhoto({breed}) {
        const {loading, error, data} = useQuery(listQuery, {
            variables: {breed},
        })
    }
    handleSubmit(event) {
        event.preventDefault();
        const dataInput = new FormData(event.target);
        const stringData = dataInput.get('search');

        return stringData
      }

    render() {
        const {list} = this.props;
        return (

            <Fragment>
                <DescriptionContain>
                    <ImageLogo src={imageTop} />
                    <FormSearch onSubmit={this.handleSubmit}>
                        <input name="search" placeholder="Search characters" />
                        <button type="submit">Search</button>
                    </FormSearch>
                </DescriptionContain>

                <ContainerList>
                 {/* {
                    !list.loading ?  
                    list.characters?.results?.map(function(items) {

                        return <Cards 
                                name={items.name}
                                image={items.image}
                                species={items.species}
                            />

                    }) : <p>CARREGANDO....</p>
                } */}
                </ContainerList>


            </Fragment>
        )
    }
}


const listQuery = gql`
    query listQuery($search: String!){
        characters(page: 1, filter: { name:  }) {
            results {
                name
                image
                species
            }
        }
    }
`

export default graphql(listQuery, {
    name: 'list'
})(Description);
