import React, { useState, Fragment } from 'react'
import { DescriptionContain, ImageLogo, FormSearch, ContainerList} from './Style'
import Cards from './../cards/Cards'
import imageTop from '../../img/image-top.svg'
import listQuery from '../../services/query/querys'
import { graphql, Query } from 'react-apollo'
import { useQuery } from '@apollo/client';

const Description = function (props) {
    const [name, setName] = useState('')
    const { data, loading, error } = useQuery(listQuery, {
        variables: { name },       
    });


    return (
        <Fragment>
        <DescriptionContain>
            <ImageLogo src={imageTop} />
            <FormSearch>
                <input 
                    name="search" 
                    placeholder="Search characters"
                />
                <button 
                    type="submit"
                    onClick={async (event) => {
                        setName(event.target.value)
                    }}
                >Search</button>

            </FormSearch>
        </DescriptionContain>

        <ContainerList>
           <Cards 
                name='teste'
                image='teste'
                species='teste'
                key='teste'
            />
        </ContainerList>


    </Fragment>
    )
}

export default graphql(listQuery, {
    name: 'list'
})(Description);
