import styled from 'styled-components'

export const ContainCard = styled.div `
    max-width: 254px;
    position: relative;
    img {
        position: relative;
        top: 0;
        left: 0;
        z-index: 1;
    }
    .infos-card {
        position: absolute;
        width: 100%;
        bottom: 0;
        background-color: #1B1B1B;
        color: var(--white);
    }
`
