import styled from 'styled-components'

export const DescriptionContain = styled.div `
    font-weight: 300;
    h1 {
        font-size: 2.5rem;
        line-height: 4rem;
        text-align: center;
        color: var(--green);
        font-weight: 300;
    }

    p {
        font-size: 1.4rem;
        line-height: 2.6rem;
        text-align: center;
        color: var(--black)
    }
`

export const ColorsContain = styled.ul `
    display: flex;
    width: 100%;
    flex-flow: wrap;
    justify-content: center;
    li {
        margin: 0.5rem 0.2rem;
        font-size: 1.2rem;
        color: var(--black);
        &::before {
            border-radius: 50%;
            content: '';
            display: block;
            width: 15px;
            height: 15px;
            margin-top: 5px;
        }
        &.circle-green {
            &::before {
                background-color: var(--green)
            }
        }
        &.circle-black {
            &::before {
                background-color: var(--black)
            }
        }
        &.circle-rose {
            &::before {
                background-color: var(--rose)
            }
        }
        &.circle-yellow {
            &::before {
                background-color: var(--yellow)
            }
        }
        &.circle-purple {
            &::before {
                background-color: var(--purple)
            }
        }
    }
`
